mapper.select:
SELECT
  COUNT(m.id)
FROM
  sys_message m
INNER JOIN sys_message_read_log mr ON m.id = mr.message_id
WHERE m.is_deleted = 0
  and mr.is_deleted = 0
  and mr.target_user_id = #{userId}

    AND m.data_source = #{dataSource}


    AND m.title LIKE CONCAT('%', #{title}, '%')


    AND m.gmt_create >= #{startTime}


    AND m.gmt_create <= #{endTime}

SELECT
    m.*,mr.is_read AS isRead,mr.target_user_id AS targetUserId
FROM
    sys_message m
        INNER JOIN sys_message_read_log mr ON m.id = mr.message_id
WHERE m.is_deleted = 0
and mr.is_deleted = 0
and mr.target_user_id = #{userId}

    AND m.data_source = #{dataSource}


    AND m.title LIKE CONCAT('%', #{title}, '%')


    AND m.gmt_create >= #{startTime}


    AND m.gmt_create <= #{endTime}
